import { useState } from 'react';
import dictation from './Dictation.module.css'

const Dictation = ({formData}) => {

const [randomWord, setRandomWord] = useState('')

const getRandomTranslation = () => {
    if (formData && formData.sentences && formData.sentences.length > 0) {
        const randomIndex = Math.floor(Math.random() * formData.sentences.length);
        setRandomWord(formData.sentences[randomIndex])
    } else {
        console.error('formData or formData.sentence is undefined or empty')
    }
}

    return(
        <div className={dictation.dictation}>
            <button onClick={getRandomTranslation}>Start dication</button>
            {randomWord && <p>{randomWord.sentence} - {randomWord.translation}</p>}
        </div>
    )
}

export default Dictation;
