import search from './Search.module.css'
import loupe from './../../../../images/loupe.png'
import { useState } from 'react'

const Search = ( {formData = [], onSearch } ) => {

const [value, setValue] = useState('')

const handleSearch = (e) => {
    const searchValue = e.target.value;
    setValue(searchValue)
        const filteredWords = formData.filter(word =>
            word.word && word.word.toLowerCase().includes(searchValue.toLowerCase())
        );
        onSearch(filteredWords);
    }

    return(
        <div className={search.search}>
            <form className={search.search__form}>
                <input 
                    type='text' 
                    placeholder='Search the word' 
                    className={search.search__form_input}
                    onChange={(e) => setValue(e.target.value)}>
                </input>
                <img src={loupe} alt='Loupe' className={search.search__form_img}></img>
            </form>
        </div>
    )
}

export default Search;
