import React, { useState, useEffect } from 'react';
import editPopup from './EditPopup.module.css'; // Стили не изменены
import close from './../../../../../images/close.png';

const EditPopup = ({ formData, onClose, addField, handleInput, removeField, handlePublish }) => {
    const [localFormData, setLocalFormData] = useState(formData);

    useEffect(() => {
        setLocalFormData(formData);
    }, [formData]);

    const handleLocalInput = (e, field, index) => {
        handleInput(e, field, index); // Передача изменения в родительский компонент
    };

    return (
        <div className={editPopup.editPopup} onClick={(e) => e.stopPropagation()}>
            <div className={editPopup.editPopup__content}>
                <button className={editPopup.editPopup__content_close} onClick={onClose}>
                    <img src={close} alt='Close' />
                </button>
                <form className={editPopup.editPopup__form}>
                    <div className={editPopup.editPopup__field}>
                        <label>Word</label>
                        <input
                            value={localFormData.word}
                            onChange={(e) => handleLocalInput(e, 'word')}
                        />
                    </div>
                    {localFormData.sentences.map((sentence, idx) => (
                        <div key={idx}>
                            <div className={editPopup.editPopup__field}>
                                <label>Sentence</label>
                                <input
                                    value={sentence.sentence}
                                    onChange={(e) => handleLocalInput(e, 'sentence', idx)}
                                />
                            </div>
                            <div className={editPopup.editPopup__field}>
                                <label>Translation</label>
                                <input
                                    value={sentence.translation}
                                    onChange={(e) => handleLocalInput(e, 'translation', idx)}
                                />
                            </div>
                            <button type="button" onClick={() => removeField(idx)}>
                                Remove Sentence
                            </button>
                        </div>
                    ))}
                    <button type="button" onClick={addField}>
                        Add Sentence
                    </button>
                    <button type="button" onClick={handlePublish}>
                        Done
                    </button>
                </form>
            </div>
        </div>
    );
};

export default EditPopup;
