import { useEffect, useState } from 'react';
import mywordspopup from './MyWordsPopup.module.css'

const MyWordsPopup = () => {

    const [formData, setFormData] = useState({
        word: '',
        sentences: [
            {
                sentence: '',
                translation: ''
            }
        ]
    })

    const [errors, setErrors] = useState({
        word: "Field can't be empty",
        sentences: [{ sentence: "Field can't be empty", translation: "Field can't be empty" }]
    })

    const [dirtyFields, setDirtyFields] = useState({
        word: false,
        sentences: [{ sentence: false, translation: false }]
    })

    const [formValid, setFormValid] = useState(false)

    useEffect(() => {
        const formHasErrors = errors.word || errors.sentences.some(e => e.sentence || e.translation);
        setFormValid(!formHasErrors)
    }, [errors]);

    const handleInput = (e) => {
        const {name, value} = e.target;
        if (name === 'word') {
        setFormData(prevFormData => ({
            ...prevFormData,
            [name]: value
        }));
        setErrors(prevErrors => ({
            ...prevErrors,
            [name]: value ? '' : "Field can't be empty"
        }))
    } else {
        const index = parseInt(e.target.dataset.index, 10);
        const fieldName = name.split('-')[1];
        const updatedSentences = [...formData.sentences];
        updatedSentences[index][fieldName] = value;
        setFormData(prevFormData => ({
            ...prevFormData,
            sentences: updatedSentences
        }));
        const updatedErrors = [...errors.sentences];
        updatedErrors[index][fieldName] = value ? '' : "Field can't be empty";
        setErrors(prevErrors => ({
            ...prevErrors,
            sentences: updatedErrors
        }));
    }
}

    const blurHandler = (e) => {
        const { name } = e.target;
        if (name === 'word') {
            setDirtyFields(prevDirtyFields => ({
                ...prevDirtyFields,
                [name]: true
            }));
        } else {
            const index = parseInt(e.target.dataset.index, 10);
            const fieldName = name.split('-')[1];
            const updatedDirtyFields = [...dirtyFields.sentences];
            updatedDirtyFields[index][fieldName] = true;
            setDirtyFields(prevDirtyFields => ({
                ...prevDirtyFields,
                sentences: updatedDirtyFields
            }));
        }
    }

    const addField = (e) => {
        setFormData(prevFormData => ({
            ...prevFormData,
            sentences: [...prevFormData.sentences, { sentence: '', translation: '' }]
        }));
        setErrors(prevErrors => ({
            ...prevErrors,
            sentences: [...prevErrors.sentences, { sentence: "Field can't be empty", translation: "Field can't be empty" }]
        }));
        setDirtyFields(prevDirtyFields => ({
            ...prevDirtyFields,
            sentences: [...prevDirtyFields.sentences, { sentence: false, translation: false }]
        }));
    }

    const removeField = (index) => {
        if (formData.sentences.length > 1) {
            setFormData(prevFormData => ({
                ...prevFormData,
                sentences: prevFormData.sentences.filter((_, i) => i !== index)
            }));
            setErrors(prevErrors => ({
                ...prevErrors,
                sentences: prevErrors.sentences.filter((_, i) => i !== index)
            }));
            setDirtyFields(prevDirtyFields => ({
                ...prevDirtyFields,
                sentences: prevDirtyFields.sentences.filter((_, i) => i !== index)
            }));
        }
    };

    return(
        <div className={mywordspopup.mywordspopup}>
            <div className={mywordspopup.mywordspopup__wrapper}>
                <div className={mywordspopup.mywordspopup__content}>
                    <div className={mywordspopup.mywordspopup__inputs}>
                        <input onChange={handleInput} onBlur={blurHandler} name='word' type='text' placeholder='word' value={formData.word}></input>
                        {dirtyFields.word && errors.word && <div style={{color: 'red', fontSize: '12px'}}>{errors.word}</div>}
                        {formData.sentences.map((sentence, index) => (
                            <div key={index} style={{ display: 'flex'}}>
                                <div className={mywordspopup.mywordspopup__inputs_repeated}>
                                    <input onChange={handleInput} onBlur={blurHandler} name={`sentence-${index}`} type='text' placeholder='sentence with word' value={formData.sentences[index].sentence || ''} data-index={index}></input>
                                    {dirtyFields.sentences[index].sentence && errors.sentences[index].sentence && <div style={{color: 'red', fontSize: '12px'}}>{errors.sentences[index].sentence}</div>}
                                    <input onChange={handleInput} onBlur={blurHandler} name={`translation-${index}`} type='text' placeholder='sentence translation' value={formData.sentences[index].translation || ''} data-index={index}></input>
                                    {dirtyFields.sentences[index].translation && errors.sentences[index].translation && <div style={{ color: 'red', fontSize: '12px' }}>{errors.sentences[index].translation}</div>}
                                </div>
                                {formData.sentences.length > 1 && (
                                <span style={{ cursor: 'pointer', marginLeft: '10px' }} onClick={() => removeField(index)}>â€“</span>
                                )}
                            </div>
                ))}
                </div>
                    <div className={mywordspopup.mywordspopup__buttons}>
                        <button onClick={addField}>add one more sentence</button>
                        <button disabled={!formValid}>done</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default MyWordsPopup;
