import { useState, useEffect, useRef } from 'react';
import editPopup from './EditPopup.module.css';
import close from './../../../../../images/close.png';

const EditPopup = ({ formData, onClose, addField, handleInput, removeField, index, handlePublish, setShowEditPopup }) => {
    const [localFormData, setLocalFormData] = useState({ ...formData });
    const [sentenceErrors, setSentenceErrors] = useState([]);
    const [translationErrors, setTranslationErrors] = useState([]);
    const [editFormValid, setEditFormValid] = useState(false);
    const [wordErrors, setWordErrors] = useState('');

    const sidebarRef = useRef(null); // Ссылка на сайдбар

    useEffect(() => {
        const handleClickOutside = (event) => {
            // Проверяем, был ли клик за пределами сайдбара
            if (isSidebarOpen && sidebarRef.current && !sidebarRef.current.contains(event.target)) {
                toggleSidebar(); // Закрываем сайдбар
            }
        };

        // Добавляем слушатель кликов по документу
        document.addEventListener('mousedown', handleClickOutside);

        // Очищаем слушатель при размонтировании компонента
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [isSidebarOpen, toggleSidebar]);

    useEffect(() => {
        validateForm();
    }, [localFormData]);

    useEffect(() => {
        setLocalFormData({ ...formData });
    }, [formData]);

    const validateForm = () => {
        const newSentenceErrors = [];
        const newTranslationErrors = [];
        const newWordErrors = [];

        localFormData.sentences.forEach((sentence, i) => {
            if (!sentence.sentence) {
                newSentenceErrors[i] = "Sentence field can't be empty";
            } else {
                newSentenceErrors[i] = "";
            }
            if (!sentence.translation) {
                newTranslationErrors[i] = "Translation field can't be empty";
            } else {
                newTranslationErrors[i] = "";
            }
        });

        if (!localFormData.word) {
            newWordErrors[0] = "Word field can't be empty";
        } else {
            newWordErrors[0] = "";
        }

        setSentenceErrors(newSentenceErrors);
        setTranslationErrors(newTranslationErrors);
        setWordErrors(newWordErrors);

        const isFormValid = newSentenceErrors.every(err => !err) && newTranslationErrors.every(err => !err) && newWordErrors.every(err => !err);
        setEditFormValid(isFormValid);
    };


    const handleLocalInput = (e, index, field) => {
        const { value } = e.target;
    
        if (typeof field !== 'string') {
            console.error('Expected string for field, but got:', typeof field);
            return;
        }
    
        setLocalFormData(prevData => {
            const updatedData = { ...prevData };
            if (field === 'word') {
                updatedData.word = value;
            } else {
                const [fieldType, idx] = field.split('-');
                if (fieldType && idx) {
                    updatedData.sentences[idx][fieldType] = value;
                }
            }
            return updatedData;
        });
    };

    const handlePublishInEdit = () => {
        handleInput({ target: { value: localFormData.word } }, index, 'word');
        localFormData.sentences.forEach((sentence, i) => {
            handleInput({ target: { value: sentence.sentence } }, index, `sentence-${i}`);
            handleInput({ target: { value: sentence.translation } }, index, `translation-${i}`);
        });
        handlePublish();
        console.log('it does not work')
        setShowEditPopup(false);
    };

    const handleClose = () => {
        setLocalFormData({ ...formData }); // Сбрасываем изменения
        setShowEditPopup(false);
        onClose()
    };

    return (
        <div className={editPopup.editPopup}>
            <div ref={sidebarRef}  className={editPopup.editPopup__wrapper}>
                <div className={editPopup.editPopup__content}>
                    <button className={editPopup.editPopup__content_close} onClick={handleClose}>
                        <img src={close} alt='Close' />
                    </button>
                    <div className={editPopup.editPopup__inputs}>
                        <input
                            onChange={(e) => handleLocalInput(e, index, 'word')}
                            type='text'
                            placeholder='word'
                            value={localFormData.word}
                            className={editPopup.editPopup__input}
                        />
                        {wordErrors[0] && <div className={editPopup.editPopup__error}>{wordErrors[0]}</div>}
                        {localFormData.sentences.map((sentence, i) => (
                            <div key={i} className={editPopup.editPopup__sentenceContainer}>
                                <div className={editPopup.editPopup__inputs_repeated}>
                                    <input
                                        onChange={(e) => handleLocalInput(e, index, 'sentence-' +i)}
                                        type='text'
                                        placeholder='sentence with word'
                                        value={sentence.sentence}
                                        className={editPopup.editPopup__input}
                                    />
                                    {sentenceErrors[i] && <div className={editPopup.editPopup__error}>{sentenceErrors[i]}</div>}
                                    <input
                                        onChange={(e) => handleLocalInput(e, index, 'translation-' +i)}
                                        type='text'
                                        placeholder='sentence translation'
                                        value={sentence.translation}
                                        className={editPopup.editPopup__input}
                                    />
                                    {translationErrors[i] && <div className={editPopup.editPopup__error}>{translationErrors[i]}</div>}
                                </div>
                                {localFormData.sentences.length > 1 && (
                                    <span
                                        className={editPopup.editPopup__removeField}
                                        onClick={() => removeField(i)}
                                    >
                                        –
                                    </span>
                                )}
                            </div>
                        ))}
                    </div>
                    <div className={editPopup.editPopup__buttons}>
                        <button onClick={addField} className={editPopup.editPopup__addButton}>add one more sentence</button>
                        <button onClick={handlePublishInEdit} disabled={!editFormValid} className={editPopup.editPopup__doneButton}>done</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EditPopup;
