import { useEffect, useState } from 'react';
import editpopup from './EditPopup.module.css';  // Стили из CSS модуля
import close from './../../../../../src/images/close.png';  // Изображение для кнопки закрытия

const EditPopup = ({ onClose, index, formData, handleInput, blurHandler, addField, removeField, handlePublish }) => {
    const [localData, setLocalData] = useState({ word: '', sentences: [] });

    useEffect(() => {
        // Инициализация локального состояния при открытии попапа
        setLocalData(formData);
    }, [formData]);

    const handleLocalInput = (e, field) => {
        const { value } = e.target;
        if (field === 'word') {
            setLocalData((prev) => ({ ...prev, word: value }));
        } else {
            const [fieldType, idx] = field.split('-');
            setLocalData((prev) => {
                const updatedSentences = [...prev.sentences];
                updatedSentences[idx][fieldType] = value;
                return { ...prev, sentences: updatedSentences };
            });
        }
    };

    const handleSave = () => {
        handlePublish(localData, index); // Передача локальных данных в родительский компонент
        onClose();
    };

    return (
        <div className={editpopup.editpopup}>
            <div className={editpopup.editpopup__wrapper}>
                <div className={editpopup.editpopup__content}>
                    <button className={editpopup.editpopup__content_close} onClick={onClose}>
                        <img src={close} alt='Close' />
                    </button>
                    <div className={editpopup.editpopup__inputs}>
                        <input
                            className={editpopup.editpopup__input_word}
                            onChange={(e) => handleLocalInput(e, 'word')}
                            name='word'
                            type='text'
                            placeholder='word'
                            value={localData.word}
                        />
                        {localData.sentences.map((sentence, i) => (
                            <div key={i} className={editpopup.editpopup__sentence_container}>
                                <input
                                    className={editpopup.editpopup__input_sentence}
                                    onChange={(e) => handleLocalInput(e, `sentence-${i}`)}
                                    name={`sentence-${i}`}
                                    type='text'
                                    placeholder='sentence with word'
                                    value={sentence.sentence}
                                />
                                <input
                                    className={editpopup.editpopup__input_translation}
                                    onChange={(e) => handleLocalInput(e, `translation-${i}`)}
                                    name={`translation-${i}`}
                                    type='text'
                                    placeholder='sentence translation'
                                    value={sentence.translation}
                                />
                                {localData.sentences.length > 1 && (
                                    <span
                                        className={editpopup.editpopup__remove_button}
                                        onClick={() => removeField(index, i)}
                                    >
                                        –
                                    </span>
                                )}
                            </div>
                        ))}
                    </div>
                    <div className={editpopup.editpopup__buttons_container}>
                        <button className={editpopup.editpopup__add_button} onClick={addField}>
                            add one more sentence
                        </button>
                        <button className={editpopup.editpopup__done_button} onClick={handleSave}>
                            done
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EditPopup;
