import sidebar from './Sidebar.module.css'
import { NavLink } from 'react-router-dom'
// import MyAccount from './MyAccount/MyAccount'
// import user_photo from './../../images/user-photo.png'
import { useState } from 'react'
import User from './User/User'
import BurgerMenu from '../Sidebar/BurgerMenu/BurgerMenu'

const Sidebar = ({isSidebarOpen, toggleSidebar}) => {

    // const [showAccountPopup, setShowAccountPopup] = useState(false)

    // const handleOpenAccountPopup = () => {
    //     setShowAccountPopup(true);
    // };

    // const handleCloseAccountPopup = () => {
    //     setShowAccountPopup(false);
    //     onClose()
    // }

    return(
        <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
            <BurgerMenu toggleSidebar={toggleSidebar} />
            <div className={sidebar.sidebar__user} >
                {/* onClick={handleOpenAccountPopup}> */}
                {/* {showAccountPopup && <MyAccount onClose={handleCloseAccountPopup} />} */}
                    <User />
                </div>
                <div className={sidebar.sidebar__pages}>
                    <NavLink to='/languages'>My languages</NavLink>
                    <NavLink to='/words'>My words</NavLink>
                    <NavLink to='/dictation'>Dictation</NavLink>
                    <NavLink to='/settings'>Settings</NavLink>
                </div>
            </div>
    )
}

export default Sidebar;
