import React, { useState } from 'react';
import burger from './BurgerMenu.module.css';

const BurgerMenu = ({ toggleSidebar }) => {
    const [isOpen, setIsOpen] = useState(false);
  
    const handleMenuClick = () => {
      setIsOpen(!isOpen);
      toggleSidebar();
    };
  
    return (
      <div className={`${burger.burger_menu} ${isOpen ? burger.open : ''}`} onClick={handleMenuClick}>
        <div className={burger.burger_bar}></div>
        <div className={burger.burger_bar}></div>
        <div className={burger.burger_bar}></div>
      </div>
    );
  };

export default BurgerMenu;
