import { useState } from 'react';
import dictation from './Dictation.module.css'

const Dictation = ({formData}) => {

const [randomWord, setRandomWord] = useState('')

const getRandomTranslation = (sentence, index) => {
    const randomIndex = Math.floor(Math.random() * formData.sentences.map((sentence, index).length))
    setRandomWord(formData.sentences[randomIndex])
}

    return(
        <div className={dictation.dictation}>
            <button onClick={getRandomTranslation}>Start dication</button>
            {randomWord && <p>{randomWord}</p>}
        </div>
    )
}

export default Dictation;
