import { useState } from 'react';
import dictation from './Dictation.module.css'

const Dictation = ({formData}) => {

const [randomTranslation, setRandomTranslation] = useState('')

const getRandomTranslation = () => {
    if (formData && formData.length > 0) {
        const allSentences = formData.flatMap(word => word.sentences)
        const randomIndex = Math.floor(Math.random() * allSentences.length);
        setRandomTranslation(allSentences[randomIndex].translation)
    } else {
        console.error('formData is undefined or empty')
    }
}

    return(
        <div className={dictation.dictation}>
            <button onClick={getRandomTranslation}>Start dication</button>
            {randomTranslation && <p>{randomTranslation}</p>}
        </div>
    )
}

export default Dictation;
