import { useEffect, useState } from 'react';
import mywordspopup from './MyWordsPopup.module.css'

const MyWordsPopup = () => {

    const [formData, setFormData] = useState({
        word: '',
        sentences: [
            {
                sentence: '',
                translation: ''
            }
        ]
    })

    const [errors, setErrors] = useState({
        word: "Field can't be empty",
        sentence: "Field can't be empty",
        translation: "Field can't be empty"
    })

    const [dirtyFields, setDirtyFields] = useState({
        word: false,
        sentence: false,
        translation: false
    })

    const [formValid, setFormValid] = useState(false)

    useEffect(() => {
        const formHasErrors = Object.values(errors).some(errors => errors)
        setFormValid(!formHasErrors)
    }, [errors]);

    const handleInput = (e) => {
        const {name, value} = e.target;
        setFormData(prevFormData => ({
            ...prevFormData,
            [name]: value
        }));
        setErrors(prevErrors => ({
            ...prevErrors,
            [name]: value ? '' : "Field can't be empty"
        }))
    };

    const blurHandler = (e) => {
        const { name } = e.target;
        setDirtyFields(prevDirtyFields => ({
            ...prevDirtyFields,
            [name]: true
        }))
    }

    return(
        <div className={mywordspopup.mywordspopup}>
            <div className={mywordspopup.mywordspopup__wrapper}>
                <div className={mywordspopup.mywordspopup__content}>
                    <div className={mywordspopup.mywordspopup__inputs}>
                        <input onChange={handleInput} onBlur={blurHandler} name='word' type='text' placeholder='word' value={formData.word}></input>
                        {dirtyFields.word && errors.word && <div style={{color: 'red'}}>{errors.word}</div>}
                        <input onChange={handleInput} onBlur={blurHandler} name='sentence' type='text' placeholder='sentence with word' value={formData.sentence}></input>
                        {dirtyFields.sentence && errors.sentence && <div style={{color: 'red', fontSize: '12px'}}>{errors.sentence}</div>}
                        <input onChange={handleInput} onBlur={blurHandler} name='translation' type='text' placeholder='sentence translation'></input>
                        {dirtyFields.translation && errors.translation && <div style={{color: 'red'}}>{errors.translation}</div>}
                    </div>
                    <div className={mywordspopup.mywordspopup__buttons}>
                        <button>add one more sentence</button>
                        <button disabled={!formValid}>done</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default MyWordsPopup;
