import editPopup from './EditPopup.module.css';
import close from './../../../../../images/close.png';
import { useState } from 'react';


const EditPopup = ({ formData, onClose, addField, handleInput, removeField, index }) => {

    const [sentenceDirty, setSentenceDirty] = useState(false)
    const [translationdDirty, setTranslationDirty] = useState(false)
    const [localFormData, setLocalFormData] = useState({...formData});
    const [sentenceError, setSentenceError] = useState("Sentence field can't be empty");
    const [translationError, setTranslationError] = useState("Translation field can't be empty");

    if (!formData || typeof formData.word === 'undefined' || !formData.sentences) {
        return null;
    }

    const handleLocalInput = (e, index, field) => {
        const { value } = e.target;
        setLocalFormData(prevData => {
            const updatedData = { ...prevData };
            if (field === 'word') {
                updatedData.word = value;
            } else {
                const [fieldType, idx] = field.split('-');
                updatedData.sentences[idx][fieldType] = value;
            }
            return updatedData;
        });
    };

    const blurHandler = (e) => {
        switch (e.target.name) {
            case 'sentence':
                setSentenceDirty(true)
                break
            case 'tanslation':
                setTranslationDirty(true)
                break
        }
    }

    const handlePublishInEdit = () => {
        handleInput({ target: { value: localFormData.word } }, index, 'word');
        localFormData.sentences.forEach((sentence, i) => {
            handleInput({ target: { value: sentence.sentence } }, index, `sentence-${i}`);
            handleInput({ target: { value: sentence.translation } }, index, `translation-${i}`);
        });
        onClose();
    };

    return (
        <div className={editPopup.editPopup}>
            <div className={editPopup.editPopup__wrapper}>
                <div className={editPopup.editPopup__content}>
                    <button className={editPopup.editPopup__content_close} onClick={onClose}>
                        <img src={close} alt='Close' />
                    </button>
                    <div className={editPopup.editPopup__inputs}>
                        <input
                            onChange={(e) => handleLocalInput(e, index, 'word')}
                            onBlur={(e) => blurHandler(e, index, 'word')}
                            type='text'
                            placeholder='word'
                            value={localFormData.word}
                        />
                        {localFormData.sentences.map((sentence, i) => (
                            <div key={i} style={{ display: 'flex' }}>
                                <div className={editPopup.editPopup__inputs_repeated}>
                                    <input
                                        onChange={(e) => handleLocalInput(e, index, `sentence-${i}`)}
                                        onBlur={(e) => blurHandler(e, index, `sentence-${i}`)}
                                        name={`sentence-${i}`}
                                        type='text'
                                        placeholder='sentence with word'
                                        value={sentence.sentence}
                                        data-index={i}
                                    />
                                    <input
                                        onChange={(e) => handleLocalInput(e, index, `translation-${i}`)}
                                        onBlur={(e) => blurHandler(e, index, `translation-${i}`)}
                                        name={`translation-${i}`}
                                        type='text'
                                        placeholder='sentence translation'
                                        value={sentence.translation}
                                        data-index={i}
                                    />
                                </div>
                                {localFormData.sentences.length > 1 && (
                                    <span style={{ cursor: 'pointer', marginLeft: '10px' }} onClick={() => removeField(i)}>–</span>
                                )}
                            </div>
                        ))}
                    </div>
                    <div className={editPopup.editPopup__buttons}>
                        <button onClick={addField}>add one more sentence</button>
                        <button onClick={() => handlePublishInEdit(index)}>done</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EditPopup;
