import { useEffect, useState } from 'react';
import mywordspopup from './MyWordsPopup.module.css'

const MyWordsPopup = () => {

    // const [formData, setFormData] = useState({
    //     word: '',
    //     sentences: [
    //         {
    //             sentence: '',
    //             translation: ''
    //         }
    //     ]
    // })

    const [formData, setFormData] = useState('')
    const [field, setField] = useState('')
    const [fieldEmpty, setFieldEmpty] = useState("Field can't be empty");
    const [fieldDirty, setFieldDirty] = useState(false)
    const [formValid, setFormValid] = useState(false)

    useEffect(() => {
        if (fieldEmpty) {
            setFormValid(false)
        } else {
            setFormValid(true)
        }
    }, [fieldEmpty])

    // function handleChange(event) {
    //     const {name, value, type} = event.target;
    //     setFormData((prevFormData) => ({
    //         ...prevFormData,
    //     }))
    // }

    function handleInput(event) {
        setField(event.target.value)
        if (formData.word === '') {
            setFieldEmpty("Field can't be empty")
        } else (
            setFieldEmpty('')
        )
    }

    const blurHandler = (e) => {
        switch (e.target.name) {
            case 'word':
                setFieldDirty(true)
                break
            case 'sentence':
                setFieldDirty(true)
                break
            case 'translation':
                setFieldDirty(true)
                break
        }
    }

    return(
        <div className={mywordspopup.mywordspopup}>
            <div className={mywordspopup.mywordspopup__wrapper}>
                <div className={mywordspopup.mywordspopup__content}>
                    <div className={mywordspopup.mywordspopup__inputs}>
                        <input onChange={event => handleInput(event)} name='word' type='text' placeholder='word'></input>
                        {(fieldDirty && fieldEmpty) && <div style={{color: 'red'}}>{fieldEmpty}</div>}
                        <input onChange={event => handleInput(event)} name='sentence' type='text' placeholder='sentence with word'></input>
                        {(fieldDirty && fieldEmpty) && <div style={{color: 'red'}}>{fieldEmpty}</div>}
                        <input onChange={event => handleInput(event)} name='translation' type='text' placeholder='sentence translation'></input>
                        {(fieldDirty && fieldEmpty) && <div style={{color: 'red'}}>{fieldEmpty}</div>}
                    </div>
                    <div className={mywordspopup.mywordspopup__buttons}>
                        <button>add one more sentence</button>
                        <button disabled={!formValid}>done</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default MyWordsPopup;
